from .Question import Question
import random

class MultiQuestion(Question):
    def __init__(self, correct, incorrect, options, question_num):
        super().__init__(correct, question_num)
        self.answer_positions = random.sample(range(0, 4), 4)
        self.incorrect = incorrect
        self.options = options

    def get_incorrect(self):
        return self.incorrect

    def get_options(self):
        return self.options

    def get_answer_positions(self):
        return self.answer_positions
